    case count
      when 1
        Prescriptions[id.to_s+"drug1"] = drug_name
        Prescriptions[id.to_s+"dosage1"] = prescribed_drug_dosage_hash[drug_name]
        Prescriptions[id.to_s+"freq1"] = prescribed_drug_frequency_hash[drug_name]
